</code></pre> decode.php<pre><code>&lt;?php

$url = "https://api.hashlix.com/api/v1/msisdn/decrypt/";
$data = array("msisdn_hash" =&gt; "your_hash_here");

$payload = json_encode($data);

$ch = curl_init($url);

curl_setopt($ch, CURLOPT_HTTPHEADER, array(
    'X-Api-Key: your-api-key',
    'Content-Type: application/json'
));
curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
curl_setopt($ch, CURLOPT_POSTFIELDS, $payload);

$response = curl_exec($ch);
curl_close($ch);

echo $response;
</code></pre> decode.js<pre><code>const axios = require('axios');

const url = 'https://api.hashlix.com/api/v1/msisdn/decrypt/';

const payload = {
  msisdn_hash: 'your_hash_here'
};

const headers = {
  'X-Api-Key': 'your-api-key',
  'Content-Type': 'application/json'
};

axios.post(url, payload, { headers })
  .then(response =&gt; {
    console.log(response.data);
  })
  .catch(error =&gt; {
    console.error('Error:', error.response?.data || error.message);
  });
</code></pre> decode.go<pre><code>package main

import (
    "bytes"
    "encoding/json"
    "fmt"
    "io"
    "net/http"
)

func main() {
    url := "https://api.hashlix.com/api/v1/msisdn/decrypt/"

    payload := map[string]string{"msisdn_hash": "your_hash_here"}
    jsonData, _ := json.Marshal(payload)

    req, _ := http.NewRequest("POST", url, bytes.NewBuffer(jsonData))
    req.Header.Set("X-Api-Key", "your-api-key")
    req.Header.Set("Content-Type", "application/json")

    client := &amp;http.Client{}
    resp, err := client.Do(req)

    if err != nil {
        fmt.Println("Error:", err)
        return
    }
    defer resp.Body.Close()

    body, _ := io.ReadAll(resp.Body)
    fmt.Println(string(body))
}
</code></pre>
